import { useEffect, useRef } from "react";
import axios from "axios"

export default function SendRegister({Data, dispatch, hasSent}) {
   

useEffect(function() {
if(!Data || hasSent) return 

const controller = new AbortController()
async function send() {
    const api = Data.login ? '/Login' : '/Register'
    console.log(Data)
    dispatch({type: "SEND_START"})
        try {
    const response = await axios.post(api, Data,{signal: controller.signal})
    const result = response.data 
    console.log(result)
    
} catch (error) {
    throw new Error("Fetch data error");
    dispatch({ type: "SEND_FAILURE", payload: error.message })
}
finally{

    dispatch({type: "RESET"})
}
}
send()
return ()=> controller.abort()
},[Data,hasSent, dispatch])
}
