import { useState } from "react"

export default function Form({setFormData, formData} ) {
function handleSubmit(e) {
    setFormData({location: e.target.value})
    console.log(e.target.value)
}
   

    return(
        <form >

            <input value={formData} onSubmit={handleSubmit} type="text" placeholder="Location" />
            <button onClick={handleSubmit} className="rounded-xl bg-slate-100 text-zinc-900 font-semibold shadow-lg hover:scale-110 transition duration-300 ease-out   w-24" >Check</button>
        </form>
            

     


    )



}
