import axios from "axios";
import { useEffect } from "react";

export default function SendData({ userLocation , setWeatherData, setIsError }) {

useEffect(()=>{

    async function Send() {
        if (!userLocation) { return setIsError("Something went wrong with fecthing your location") }
    
        try {
          const { latitude, longitude } = userLocation
          
          const response = await axios.post("/weather", {latitude: latitude, longitude: longitude});
          const result = await response.data;
          console.log("server response:", result.content)
    
          setWeatherData(result)
          if(!result) throw new Error("Something went wrong with fecthing your location");
        
    
        } catch (error) { console.log("Error sending Data:", error );
          setIsError(error)
        
         }
      }
      Send()
} , [userLocation, setWeatherData])
return null

}
