import { useState } from "react"
import FormCOmp from "./Form"
import Header from "../Header"
import PopUp from "./PopOut"
import JournalAndNote from "./sendFormData"
export default function App3(params) {

    const [createNote, setCreateNote] = useState(false)
    const [createJournal, setCreateJournal]  = useState(false)
    const [hideForm, setHideForm] = useState(false)
    const [hideCreateBtn, setHideCreateBtn] = useState(false)
    const [noteAndJournal, setNoteAndJournal] = useState(false)


function handleSubmission(formDATA) {
    console.log(formDATA)
    setNoteAndJournal(formDATA)
    

}

    return <div className="bg-gradient-to-b from-teal-200 to-rose-100 ">
        <Header/>
        <PopUp openPopUp={hideCreateBtn} closePopUp={setHideCreateBtn} setCreateJournal={setCreateJournal} setCreateNote={setCreateNote} setHideForm = {setHideForm} />
<div className="w-full p-5 justify-center items-center ">
    
            <button onClick={() => setHideCreateBtn((prev) => !prev)} className='bg-blue-300 text-blue-500 border border-blue-400 rounded-md px-5 py-2 hover:bg-blue-100'>Note </button>
           
</div>
     {hideForm && <FormCOmp typeOfForm={createNote ? "note" : "journal" } onSubmit = {handleSubmission} />}
     <JournalAndNote FormData={noteAndJournal}  />
     </div>
     
}
