import { useState } from "react";

export default function Calendar() {
  const options = {
    weekday: "short",
    year: "numeric",
    month: "short",
    day: "numeric",
  };

  let today = new Date();
  let month = today.getMonth() + 1;
  let days = new Date(month, today.getFullYear(), 0).getDate();

  let dateArray = Array.from({ length: days }, (_, i) => i + 1);
  let CurrentDate = today.getDate();
 
  let curentMonth = today
  const [curDate, setCurDate] = useState(CurrentDate);

  return (
    <div className="grid gap-2 grid-cols-7 w-72 h-96 p-3 font-bold bg-blue-50 opacity-85 place-items-center rounded-md  hover:scale-105 transition duration-500 ease-out">
      {dateArray.map((each) => (
        <div
          onClick={(e) => console.log(each)}
          className={` hover:scale-125 transition duration-300 ease-out rounded-lg text-center border-2 w-8 m-2 cursor-pointer ${
            each === CurrentDate
              ? "bg-orange-500 font-extrabold hover:bg-orange-700 hover:text-white"
              : "bg-orange-200 hover:bg-orange-300 hover:text-white"
          } `}
          key={each}
        >
          {" "}
          {each}{" "}
        </div>
      ))}
    </div>
  );
}
