import { useState } from "react";

export default function FormCOmp({ typeOfForm = "login" }) {
  const [valueAdd, setValueAdd] = useState({
    user: {
      email: "",
      password: "",
      username: "",
      age: "",
    },

    arthur: "",
    about: "",
    text: "",
  });

  function handleSubmit(e) {
    e.preventDefault();
    console.log(e.target);
  }

  function handleChanges(e) {
    const { name, value, id } = e.target;

    console.log([name, value, id]);
  }

  return (
    <form onSubmit={handleSubmit} className="grid grid-c">
      {typeOfForm === "login" ? (
        <>
          {" "}
          <input
            type="email"
            name="email"
            id="email"
            value={valueAdd.user.email}
            onChange={handleChanges}
          ></input>
          <input
            type="password"
            name="password"
            id="password"
            value={valueAdd.user.password}
            onChange={handleChanges}
          />
        </>
      ) : typeOfForm === "note" ? (
        <>
          {" "}
          <input
            placeholder="About"
            name="about"
            id="NAbout"
            value={valueAdd.about}
            onChange={handleChanges}
          >
            {" "}
          </input>
          <textarea
            name="text"
            id="note"
            cols="30"
            rows="10"
            value={valueAdd.text}
            onChange={handleChanges}
          ></textarea>
        </>
      ) : typeOfForm === "Register" ? (
        <>
          <input
            type="email"
            name="email"
            id="Remail"
            value={valueAdd.user.email}
            onChange={handleChanges}
          />
          <input
            type="password"
            name="password"
            id="password"
            value={valueAdd.user.password}
            onChange={handleChanges}
          />
          <input
            type="text"
            name="username"
            id="username"
            value={valueAdd.user.username}
            onChange={handleChanges}
          ></input>
          <input
            type="date"
            name="age"
            id="age"
            value={valueAdd.user.age}
            onChange={handleChanges}
          />
        </>
      ) : (
        typeOfForm === "journal" && (
          <>
            <input
              type="text"
              placeholder="give your journal a Name"
              name="about"
              id="JAbout"
              value={valueAdd.about}
              onChange={handleChanges}
            ></input>
            <input
              type="text"
              placeholder="Written by"
              id="JArthur"
              name="arthur"
              value={valueAdd.text}
              onChange={handleChanges}
            ></input>
            <textarea
              name="text"
              id="Journal"
              cols="50"
              rows="10"
              value={valueAdd.text}
              onChange={handleChanges}
            ></textarea>
          </>
        )
      )}

      <button type="submit">
        {typeOfForm === "note" || typeOfForm === "journal"
          ? "post"
          : typeOfForm === "login"
          ? "Login"
          : typeOfForm === "register"
          ? "Register"
          : "Submit"}{" "}
      </button>
    </form>
  );
}
