useEffect(function() {
if(initialMount.current){
    initialMount.current= false 
    return
}

if(!Data || hasSent) {
     console.log("this is the errer")
     console.log(D)
    return
}

const controller = new AbortController()
async function send() {
    const api = Data.login ? '/Login' : '/Register'
    console.log(Data)
    dispatch({type: "SEND_START"})
        try {
    const response = await axios.post(api, Data,{signal: controller.signal})
    const result = response.data 
    console.log(result)
    dispatch({ type: "SEND_SUCCESS", payload: result });
    
} catch (error) {
 
    dispatch({ type: "SEND_FAILURE", payload: error.message })
}
finally{
    if (!controller.signal.aborted) {
        dispatch({ type: "RESET" });
    }
}
}
send()
return ()=> controller.abort()
},[Data,hasSent, dispatch])
