import { useState } from "react";
import Calendar from "./Calendar";
import Location from "./Location";
import SendData from "./SendDataServer";
import ShowWeather from "./DisplayWeather";
import Header from "./Header";
import Accessories from "./Accessories";


export default function App() {
  const [userLocation, setUserLocation] = useState(null);
  const [weatherData, setWeatherData] = useState({});
  const [isError, setIsError] = useState("")
  const [formData, setFormData] = useState()
  const [selectedDate, setSelectedDate] = useState(null)


  const isWeatherDataEmpty = Object.keys(weatherData).length === 0;
  return  <div  className="bg-gradient-to-t from-teal-50 to-red-100 m-0 h-screen" >
 <Header />

    <div className="flex justify-around">

      {   isWeatherDataEmpty ? (
       <div >

         <Location
           userLocation={userLocation}
           setUserLocation={setUserLocation}
           isError={isError}
           setFormData = {setFormData}
           formData = {formData}

         />
       </div> 
      ) : (
        <div className="grid gap-3">
          <ShowWeather weatherData={weatherData} fromBGColor="from-sky" />

    <Accessories/>

</div>  
      )}
      <Calendar setSelectedDate = {setSelectedDate}/>
    </div>
      <SendData userLocation={userLocation} setWeatherData={setWeatherData} setIsError={setIsError} formData={formData} />

    </div>

}
